import React, { useCallback, useRef, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import { toast } from 'react-hot-toast';
import { motion } from 'framer-motion';
import { Upload, Image, AlertCircle } from 'lucide-react';
import { ProcessingState, ProcessingResult } from '../types';
import { apiService } from '../services/api';
import { validateImage } from '../utils/imageUtils';

interface ImageUploaderProps {
  onImageProcessed: (result: ProcessingResult) => void;
  onProcessingStateChange: (state: ProcessingState) => void;
}

const ImageUploader: React.FC<ImageUploaderProps> = ({ onImageProcessed, onProcessingStateChange }) => {
  const eventSourceRef = useRef<EventSource | null>(null);

  const disconnectEventSource = useCallback(() => {
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
      eventSourceRef.current = null;
    }
  }, []);

  // Cleanup EventSource on unmount
  useEffect(() => {
    return () => {
      disconnectEventSource();
    };
  }, [disconnectEventSource]);

  const connectEventSource = useCallback(() => {
    if (eventSourceRef.current?.readyState === EventSource.OPEN) {
      console.log('EventSource already connected');
      return;
    }

    console.log('Connecting to EventSource...');
    const eventSource = new EventSource('http://127.0.0.1:8002/api/progress');

    eventSource.onopen = () => {
      console.log('✅ EventSource connected for progress updates');
    };

    eventSource.onmessage = (event) => {
      console.log('📨 EventSource message received:', event.data);
      try {
        const data = JSON.parse(event.data);
        if (data.type === 'progress') {
          console.log('🔄 Progress update:', data.progress, data.message);
          // Only update if it's not the initial "Connected to progress stream" message
          if (data.message !== 'Connected to progress stream') {
            onProcessingStateChange({
              status: 'processing',
              progress: data.progress,
              message: data.message
            });
          }
        }
      } catch (error) {
        console.error('❌ Failed to parse EventSource message:', error);
      }
    };

    eventSource.onerror = (error) => {
      console.error('❌ EventSource error:', error);
    };

    eventSourceRef.current = eventSource;
  }, [onProcessingStateChange]);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];

    // Validate image
    const validation = await validateImage(file);

    if (!validation.isValid) {
      toast.error(validation.errors.join(', '));
      return;
    }


    try {
      // Connect to EventSource for real-time progress updates
      connectEventSource();

      // Wait a moment to ensure EventSource is connected before starting processing
      await new Promise(resolve => setTimeout(resolve, 100));

      // Show initial progress
      onProcessingStateChange({
        status: 'processing',
        progress: 20,
        message: 'Detecting book spines with YOLO...'
      });

      // Process image through the API (this will complete when processing is done)
      const result = await apiService.processImage(file);

      // Don't manually set progress to 100% - let the SSE stream handle it
      // The backend should send a final progress update with 100%

      // Only handle the final result processing
      onImageProcessed(result);
      toast.success(`Successfully processed ${result.total_spines || 0} spines!`);

      // Wait a bit for final progress update, then disconnect
      setTimeout(() => {
        onProcessingStateChange({
          status: 'completed',
          progress: 100,
          message: `Processing complete! Found ${result.total_spines || 0} spines with ${result.successful_matches || 0} successful matches.`
        });
        disconnectEventSource();
      }, 1000);

    } catch (error) {
      console.error('Processing error:', error);
      onProcessingStateChange({
        status: 'error',
        progress: 0,
        message: 'Processing failed',
        error: error instanceof Error ? error.message : 'Unknown error'
      });
      toast.error('Failed to process image');

      // Disconnect EventSource after error
      disconnectEventSource();
    }
  }, [onImageProcessed, onProcessingStateChange, connectEventSource, disconnectEventSource]);

  const { getRootProps, getInputProps, isDragActive, isDragReject } = useDropzone({
    onDrop,
    accept: {
      'image/jpeg': ['.jpg', '.jpeg']
    },
    maxFiles: 1,
    disabled: false
  });

  return (
    <div className="max-w-2xl mx-auto">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
        className="text-center mb-12"
      >
        <h2 className="text-4xl font-bold text-white mb-4">
          Upload Book Spine Image
        </h2>
        <p className="text-lg text-slate-300 max-w-2xl mx-auto leading-relaxed">
          Upload a high-quality image of book spines to automatically detect and identify books
        </p>
      </motion.div>

      {/* Upload Area */}
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.3, delay: 0.2 }}
        className="bg-slate-800/30 backdrop-blur-sm rounded-2xl border border-slate-600/50 shadow-lg"
      >
        <div
          {...getRootProps()}
          className={`
            border-2 border-dashed rounded-2xl p-16 text-center cursor-pointer transition-all duration-200
            ${isDragActive && !isDragReject
              ? 'border-blue-400 bg-blue-500/10'
              : isDragReject
              ? 'border-red-400 bg-red-500/10'
              : 'border-slate-400 hover:border-blue-400 hover:bg-blue-500/5'
            }
            ${false ? 'opacity-50 cursor-not-allowed' : ''}
          `}
        >
          <input {...getInputProps()} />

          <div className="space-y-4">
            {false ? (
              <div className="animate-pulse">
                <div className="w-16 h-16 bg-primary-200 rounded-full mx-auto mb-4"></div>
                <p className="text-primary-600 font-medium">Processing image...</p>
              </div>
            ) : (
              <>
                <div className="w-16 h-16 mx-auto mb-4">
                  {isDragActive && !isDragReject ? (
                    <Upload className="w-16 h-16 text-primary-500" />
                  ) : isDragReject ? (
                    <AlertCircle className="w-16 h-16 text-error-500" />
                  ) : (
                    <Image className="w-16 h-16 text-gray-400" />
                  )}
                </div>

                <div>
                  <p className="text-lg font-medium text-white mb-2">
                    {isDragActive && !isDragReject
                      ? 'Drop the image here'
                      : isDragReject
                      ? 'Invalid file type'
                      : 'Drag & drop an image here'
                    }
                  </p>
                  <p className="text-slate-300">
                    or click to select a file
                  </p>
                  <p className="text-sm text-slate-400 mt-2">
                    Only JPG images are supported
                  </p>
                </div>
              </>
            )}
          </div>
        </div>
      </motion.div>

      {/* Validation Result */}
      {/* validationResult was removed, so this block is always null */}
      {/* {validationResult && ( */}
      {/*   <motion.div */}
      {/*     initial={{ opacity: 0, y: 10 }} */}
      {/*     animate={{ opacity: 1, y: 0 }} */}
      {/*     transition={{ duration: 0.3 }} */}
      {/*     className="mt-6" */}
      {/*   > */}
      {/*     {validationResult.isValid ? ( */}
      {/*       <div className="flex items-center justify-center space-x-2 text-success-600"> */}
      {/*         <CheckCircle className="h-5 w-5" /> */}
      {/*         <span>Image validation passed</span> */}
      {/*       </div> */}
      {/*     ) : ( */}
      {/*       <div className="flex items-center justify-center space-x-2 text-error-600"> */}
      {/*         <AlertCircle className="h-5 w-5" /> */}
      {/*         <span>Image validation failed</span> */}
      {/*       </div> */}
      {/*     )} */}
      {/*   </motion.div> */}
      {/* )} */}

      {/* Requirements */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3, delay: 0.4 }}
        className="mt-12 grid grid-cols-1 md:grid-cols-3 gap-6"
      >
        <div className="text-center p-6 bg-slate-800/30 backdrop-blur-sm rounded-2xl border border-slate-600/50 shadow-lg">
          <div className="w-12 h-12 bg-blue-500 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-md">
            <span className="text-white text-lg font-bold">1</span>
          </div>
          <h3 className="font-semibold text-white mb-2">High Resolution</h3>
          <p className="text-slate-300 text-sm">Clear, sharp images work best</p>
        </div>

        <div className="text-center p-6 bg-slate-800/30 backdrop-blur-sm rounded-2xl border border-slate-600/50 shadow-lg">
          <div className="w-12 h-12 bg-indigo-500 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-md">
            <span className="text-white text-lg font-bold">2</span>
          </div>
          <h3 className="font-semibold text-white mb-2">Good Lighting</h3>
          <p className="text-slate-300 text-sm">Well-lit spines for better OCR</p>
        </div>

        <div className="text-center p-6 bg-slate-800/30 backdrop-blur-sm rounded-2xl border border-slate-600/50 shadow-lg">
          <div className="w-12 h-12 bg-purple-500 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-md">
            <span className="text-white text-lg font-bold">3</span>
          </div>
          <h3 className="font-semibold text-white mb-2">JPG Format</h3>
          <p className="text-slate-300 text-sm">Only JPEG images supported</p>
        </div>
      </motion.div>
    </div>
  );
};

export default ImageUploader;
