import React, { useState } from 'react';
import { Toaster } from 'react-hot-toast';
import { motion, AnimatePresence } from 'framer-motion';
import { Upload, BookOpen, Settings, Download } from 'lucide-react';

import { ProcessingState, ProcessingResult } from './types';
import ImageUploader from './components/ImageUploader';
import SpineVisualizer from './components/SpineVisualizer';
import BookListManager from './components/BookListManager';
import ProcessingStatus from './components/ProcessingStatus';
import FinalizePanel from './components/FinalizePanel';

const App: React.FC = () => {
  const [processingState, setProcessingState] = useState<ProcessingState>({
    status: 'idle',
    progress: 0,
    message: 'Ready to process images'
  });
  
  const [processingResult, setProcessingResult] = useState<ProcessingResult | null>(null);
  const [selectedSpineId, setSelectedSpineId] = useState<string | null>(null);
  const [isFinalized, setIsFinalized] = useState(false);

  const handleImageProcessed = (result: ProcessingResult) => {
    setProcessingResult(result);
    setProcessingState({
      status: 'completed',
      progress: 100,
      message: `Processed ${result.total_spines} spines with ${result.successful_matches} successful matches`
    });
  };

  const handleSpineSelected = (spineId: string) => {
    setSelectedSpineId(spineId);
  };

  const handleFinalize = () => {
    setIsFinalized(true);
  };

  const handleUnfinalize = () => {
    setIsFinalized(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-white">
      <Toaster position="top-right" />
      
      {/* Header */}
      <header className="border-b border-slate-700/50 bg-slate-900/50 backdrop-blur-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl shadow-lg flex items-center justify-center">
                <BookOpen className="h-7 w-7 text-white" />
              </div>
              <div>
                <h1 className="text-3xl font-bold text-white tracking-tight">
                  Spinecat
                </h1>
                <p className="text-sm text-slate-400 -mt-1">Book Spine Detection</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button className="p-3 rounded-xl bg-slate-800/50 hover:bg-slate-700/50 transition-colors duration-200 border border-slate-600/50 hover:border-slate-500/50">
                <Settings className="h-5 w-5 text-slate-300" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        <AnimatePresence mode="wait">
          {processingState.status === 'idle' && (
            <motion.div
              key="upload"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              <ImageUploader
                onImageProcessed={handleImageProcessed}
                onProcessingStateChange={setProcessingState}
              />
            </motion.div>
          )}

          {processingState.status === 'processing' && (
            <motion.div
              key="processing"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="text-center"
            >
              <ProcessingStatus state={processingState} />
            </motion.div>
          )}

          {processingState.status === 'completed' && processingResult && (
            <motion.div
              key="results"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="space-y-6"
            >
              {/* Results Header */}
              <div className="flex items-center justify-between bg-slate-800/30 backdrop-blur-sm rounded-2xl p-6 border border-slate-600/50 shadow-lg">
                <div>
                  <h2 className="text-2xl font-semibold text-white mb-3">
                    Processing Complete
                  </h2>
                  <div className="flex items-center space-x-4">
                    <span className="inline-flex items-center px-3 py-1 rounded-lg text-sm font-medium bg-blue-500/20 text-blue-300 border border-blue-400/30">
                      📚 {processingResult.total_spines} spines detected
                    </span>
                    <span className="inline-flex items-center px-3 py-1 rounded-lg text-sm font-medium bg-green-500/20 text-green-300 border border-green-400/30">
                      ✅ {processingResult.successful_matches} successful matches
                    </span>
                  </div>
                </div>
                
                {!isFinalized && (
                  <button
                    onClick={handleFinalize}
                    className="px-6 py-3 bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-400 hover:to-indigo-500 rounded-xl shadow-lg transition-all duration-200 border border-blue-400/30"
                  >
                    <div className="flex items-center space-x-2">
                      <Download className="h-5 w-5 text-white" />
                      <span className="font-semibold text-white">Finalize & Export</span>
                    </div>
                  </button>
                )}
              </div>

              {/* Main Results Layout */}
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                {/* Left: Image with Spine Regions */}
                <div className="lg:col-span-2">
                  <div className="bg-slate-800/30 backdrop-blur-sm rounded-2xl p-6 border border-slate-600/50 shadow-lg">
                    <SpineVisualizer
                      imageUrl={processingResult.image_url}
                      spineRegions={processingResult.spine_regions}
                      bookMatches={processingResult.book_matches}
                      selectedSpineId={selectedSpineId}
                      onSpineSelected={handleSpineSelected}
                      isFinalized={isFinalized}
                    />
                  </div>
                </div>

                {/* Right: Book List Management */}
                <div className="space-y-6">
                  <BookListManager
                    bookMatches={processingResult.book_matches}
                    ocrFailures={processingResult.ocr_failures}
                    selectedSpineId={selectedSpineId}
                    onSpineSelected={handleSpineSelected}
                    onBookListUpdated={(updatedMatches) => {
                      setProcessingResult(prev => prev ? {
                        ...prev,
                        book_matches: updatedMatches
                      } : null);
                      }}
                    />
                </div>
              </div>

              {/* Finalize Panel */}
              {isFinalized && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  <FinalizePanel
                    processingResult={processingResult}
                    onUnfinalize={handleUnfinalize}
                  />
                </motion.div>
              )}
            </motion.div>
          )}

          {processingState.status === 'error' && (
            <motion.div
              key="error"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className="text-center"
            >
              <div className="bg-slate-800/30 backdrop-blur-sm rounded-2xl p-8 border border-red-500/30 shadow-lg max-w-md mx-auto">
                <div className="w-16 h-16 bg-red-500 rounded-full flex items-center justify-center mx-auto mb-6 shadow-lg">
                  <span className="text-2xl">⚠️</span>
                </div>
                <h3 className="text-xl font-semibold text-white mb-3">
                  Processing Error
                </h3>
                <p className="text-slate-300 mb-6">
                  {processingState.error || 'An unexpected error occurred'}
                </p>
                <button
                  onClick={() => setProcessingState({
                    status: 'idle',
                    progress: 0,
                    message: 'Ready to process images'
                  })}
                  className="px-6 py-3 bg-red-500 hover:bg-red-600 rounded-xl shadow-lg transition-colors duration-200"
                >
                  <span className="font-semibold text-white">Try Again</span>
                </button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </main>
    </div>
  );
};

export default App;
