import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { motion } from 'framer-motion';
import { Upload, Image, AlertCircle, CheckCircle, BookOpen } from 'lucide-react';
import toast from 'react-hot-toast';

import { ProcessingState, ProcessingResult, ImageValidationResult } from '../types';
import { validateImage, processImage } from '../utils/imageUtils';

interface ImageUploaderProps {
  onImageProcessed: (result: ProcessingResult) => void;
  onProcessingStateChange: (state: ProcessingState) => void;
}

const ImageUploader: React.FC<ImageUploaderProps> = ({
  onImageProcessed,
  onProcessingStateChange
}) => {
  const [isProcessing, setIsProcessing] = useState(false);
  const [validationResult, setValidationResult] = useState<ImageValidationResult | null>(null);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];

    // Validate image
    const validation = await validateImage(file);
    setValidationResult(validation);

    if (!validation.isValid) {
      toast.error(`Image rejected: ${validation.errors.join(', ')}`);
      return;
    }

    // Process image
    setIsProcessing(true);
    onProcessingStateChange({
      status: 'uploading',
      progress: 10,
      message: 'Uploading image...'
    });

    try {
      // Simulate processing steps
      onProcessingStateChange({
        status: 'processing',
        progress: 30,
        message: 'Detecting book spines...'
      });

      await new Promise(resolve => setTimeout(resolve, 1000));

      onProcessingStateChange({
        status: 'processing',
        progress: 60,
        message: 'Running OCR on detected spines...'
      });

      await new Promise(resolve => setTimeout(resolve, 1000));

      onProcessingStateChange({
        status: 'processing',
        progress: 80,
        message: 'Matching books with library database...'
      });

      await new Promise(resolve => setTimeout(resolve, 1000));

      // For now, create a mock result - this will be replaced with actual pipeline integration
      const mockResult: ProcessingResult = {
        image_url: URL.createObjectURL(file),
        spine_regions: [
          {
            id: 'spine_1',
            x: 100,
            y: 150,
            width: 80,
            height: 200,
            rotation: 0,
            confidence: 0.95,
            coordinates: [[100, 150], [180, 150], [180, 350], [100, 350]]
          },
          {
            id: 'spine_2',
            x: 200,
            y: 150,
            width: 80,
            height: 200,
            rotation: 0,
            confidence: 0.92,
            coordinates: [[200, 150], [280, 150], [280, 350], [200, 350]]
          }
        ],
        book_matches: [
          {
            id: 'book_1',
            title: 'The Great Gatsby',
            author_name: ['F. Scott Fitzgerald'],
            first_publish_year: 1925,
            match_score: 0.95,
            match_type: 'exact',
            confidence: 0.95,
            ocr_text: 'The Great Gatsby',
            spine_region_id: 'spine_1'
          },
          {
            id: 'book_2',
            title: 'To Kill a Mockingbird',
            author_name: ['Harper Lee'],
            first_publish_year: 1960,
            match_score: 0.92,
            match_type: 'strong',
            confidence: 0.92,
            ocr_text: 'To Kill a Mockingbird',
            spine_region_id: 'spine_2'
          }
        ],
        ocr_failures: [],
        processing_time: 3.2,
        total_spines: 2,
        successful_matches: 2,
        perfect_matches: 1
      };

      onProcessingStateChange({
        status: 'completed',
        progress: 100,
        message: 'Processing complete!'
      });

      onImageProcessed(mockResult);
      toast.success('Image processed successfully!');

    } catch (error) {
      console.error('Processing error:', error);
      onProcessingStateChange({
        status: 'error',
        progress: 0,
        message: 'Processing failed',
        error: error instanceof Error ? error.message : 'Unknown error'
      });
      toast.error('Failed to process image');
    } finally {
      setIsProcessing(false);
    }
  }, [onImageProcessed, onProcessingStateChange]);

  const { getRootProps, getInputProps, isDragActive, isDragReject } = useDropzone({
    onDrop,
    accept: {
      'image/jpeg': ['.jpg', '.jpeg']
    },
    maxFiles: 1,
    disabled: isProcessing
  });

  return (
    <div className="max-w-2xl mx-auto">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="text-center mb-12"
      >
        <div className="relative inline-block mb-6">
          <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-3xl shadow-2xl flex items-center justify-center mx-auto transform rotate-6 hover:rotate-0 transition-transform duration-500">
            <BookOpen className="w-12 h-12 text-white" />
          </div>
          <div className="absolute -top-2 -right-2 w-8 h-8 bg-gradient-to-br from-emerald-400 to-teal-500 rounded-full border-4 border-white shadow-lg"></div>
        </div>
        <h2 className="text-4xl font-bold bg-gradient-to-r from-slate-800 to-slate-600 bg-clip-text text-transparent mb-4">
          Upload Book Spine Image
        </h2>
        <p className="text-xl text-slate-600 max-w-2xl mx-auto leading-relaxed">
          Upload a high-quality image of book spines to automatically detect and identify books using AI
        </p>
      </motion.div>

      {/* Upload Area */}
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="card"
      >
        <div
          {...getRootProps()}
          className={`
            border-2 border-dashed rounded-2xl p-16 text-center cursor-pointer transition-all duration-300 backdrop-blur-sm
            ${isDragActive && !isDragReject
              ? 'border-blue-400 bg-blue-50/50 shadow-lg'
              : isDragReject
              ? 'border-red-400 bg-red-50/50 shadow-lg'
              : 'border-slate-300 hover:border-blue-400 hover:bg-blue-50/30 hover:shadow-xl'
            }
            ${isProcessing ? 'opacity-50 cursor-not-allowed' : ''}
          `}
        >
          <input {...getInputProps()} />

          <div className="space-y-4">
            {isProcessing ? (
              <div className="animate-pulse">
                <div className="w-16 h-16 bg-primary-200 rounded-full mx-auto mb-4"></div>
                <p className="text-primary-600 font-medium">Processing image...</p>
              </div>
            ) : (
              <>
                <div className="w-16 h-16 mx-auto mb-4">
                  {isDragActive && !isDragReject ? (
                    <Upload className="w-16 h-16 text-primary-500" />
                  ) : isDragReject ? (
                    <AlertCircle className="w-16 h-16 text-error-500" />
                  ) : (
                    <Image className="w-16 h-16 text-gray-400" />
                  )}
                </div>

                <div>
                  <p className="text-lg font-medium text-gray-900 mb-2">
                    {isDragActive && !isDragReject
                      ? 'Drop the image here'
                      : isDragReject
                      ? 'Invalid file type'
                      : 'Drag & drop an image here'
                    }
                  </p>
                  <p className="text-gray-500">
                    or click to select a file
                  </p>
                  <p className="text-sm text-gray-400 mt-2">
                    Only JPG images are supported
                  </p>
                </div>
              </>
            )}
          </div>
        </div>
      </motion.div>

      {/* Validation Result */}
      {validationResult && (
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
          className="mt-6"
        >
          {validationResult.isValid ? (
            <div className="flex items-center justify-center space-x-2 text-success-600">
              <CheckCircle className="h-5 w-5" />
              <span>Image validation passed</span>
            </div>
          ) : (
            <div className="flex items-center justify-center space-x-2 text-error-600">
              <AlertCircle className="h-5 w-5" />
              <span>Image validation failed</span>
            </div>
          )}
        </motion.div>
      )}

      {/* Requirements */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
        className="mt-12 grid grid-cols-1 md:grid-cols-3 gap-6"
      >
        <motion.div 
          whileHover={{ y: -5, scale: 1.02 }}
          className="text-center p-6 bg-white/80 backdrop-blur-sm rounded-2xl border border-white/20 shadow-lg hover:shadow-xl transition-all duration-300"
        >
          <div className="w-12 h-12 bg-gradient-to-br from-blue-100 to-blue-200 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-md">
            <span className="text-blue-600 text-lg font-bold">1</span>
          </div>
          <h3 className="font-semibold text-slate-800 mb-2">High Resolution</h3>
          <p className="text-slate-600">Clear, sharp images work best</p>
        </motion.div>

        <motion.div 
          whileHover={{ y: -5, scale: 1.02 }}
          className="text-center p-6 bg-white/80 backdrop-blur-sm rounded-2xl border border-white/20 shadow-lg hover:shadow-xl transition-all duration-300"
        >
          <div className="w-12 h-12 bg-gradient-to-br from-emerald-100 to-emerald-200 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-md">
            <span className="text-emerald-600 text-lg font-bold">2</span>
          </div>
          <h3 className="font-semibold text-slate-800 mb-2">Good Lighting</h3>
          <p className="text-slate-600">Well-lit spines for better OCR</p>
        </motion.div>

        <motion.div 
          whileHover={{ y: -5, scale: 1.02 }}
          className="text-center p-6 bg-white/80 backdrop-blur-sm rounded-2xl border border-white/20 shadow-lg hover:shadow-xl transition-all duration-300"
        >
          <div className="w-12 h-12 bg-gradient-to-br from-indigo-100 to-indigo-200 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-md">
            <span className="text-indigo-600 text-lg font-bold">3</span>
          </div>
          <h3 className="font-semibold text-slate-800 mb-2">JPG Format</h3>
          <p className="text-slate-600">Only JPEG images supported</p>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default ImageUploader;
