import React, { useState } from 'react';
import { Toaster } from 'react-hot-toast';
import { motion, AnimatePresence } from 'framer-motion';
import { Upload, BookOpen, Settings, Download } from 'lucide-react';

import { ProcessingState, ProcessingResult } from './types';
import ImageUploader from './components/ImageUploader';
import SpineVisualizer from './components/SpineVisualizer';
import BookListManager from './components/BookListManager';
import ProcessingStatus from './components/ProcessingStatus';
import FinalizePanel from './components/FinalizePanel';

const App: React.FC = () => {
  const [processingState, setProcessingState] = useState<ProcessingState>({
    status: 'idle',
    progress: 0,
    message: 'Ready to process images'
  });
  
  const [processingResult, setProcessingResult] = useState<ProcessingResult | null>(null);
  const [selectedSpineId, setSelectedSpineId] = useState<string | null>(null);
  const [isFinalized, setIsFinalized] = useState(false);

  const handleImageProcessed = (result: ProcessingResult) => {
    setProcessingResult(result);
    setProcessingState({
      status: 'completed',
      progress: 100,
      message: `Processed ${result.total_spines} spines with ${result.successful_matches} successful matches`
    });
  };

  const handleSpineSelected = (spineId: string) => {
    setSelectedSpineId(spineId);
  };

  const handleFinalize = () => {
    setIsFinalized(true);
  };

  const handleUnfinalize = () => {
    setIsFinalized(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      <Toaster position="top-right" />
      
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-sm border-b border-white/20 shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-600 to-indigo-700 rounded-2xl shadow-lg flex items-center justify-center transform rotate-3 hover:rotate-0 transition-transform duration-300">
                  <BookOpen className="h-7 w-7 text-white" />
                </div>
                <div className="absolute -top-1 -right-1 w-5 h-5 bg-gradient-to-br from-emerald-400 to-teal-500 rounded-full border-2 border-white shadow-md"></div>
              </div>
              <div>
                <h1 className="text-4xl font-bold bg-gradient-to-r from-slate-800 to-slate-600 bg-clip-text text-transparent tracking-tight">
                  Spinecat
                </h1>
                <p className="text-sm text-slate-500 -mt-1 font-medium">AI-Powered Book Detection</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button className="p-3 rounded-xl bg-white/50 hover:bg-white/80 transition-all duration-200 shadow-sm hover:shadow-md border border-white/20">
                <Settings className="h-5 w-5 text-slate-600" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <AnimatePresence mode="wait">
          {processingState.status === 'idle' && (
            <motion.div
              key="upload"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="text-center"
            >
              <ImageUploader
                onImageProcessed={handleImageProcessed}
                onProcessingStateChange={setProcessingState}
              />
            </motion.div>
          )}

          {processingState.status === 'processing' && (
            <motion.div
              key="processing"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="text-center"
            >
              <ProcessingStatus state={processingState} />
            </motion.div>
          )}

          {processingState.status === 'completed' && processingResult && (
            <motion.div
              key="results"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="space-y-6"
            >
              {/* Results Header */}
              <div className="flex items-center justify-between bg-white/60 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-white/20">
                <div>
                  <h2 className="text-3xl font-bold bg-gradient-to-r from-slate-800 to-slate-600 bg-clip-text text-transparent">
                    Processing Complete
                  </h2>
                  <div className="flex items-center space-x-4 mt-2">
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-100 text-blue-800">
                      üìö {processingResult.total_spines} spines detected
                    </span>
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-emerald-100 text-emerald-800">
                      ‚úÖ {processingResult.successful_matches} successful matches
                    </span>
                  </div>
                </div>
                
                {!isFinalized && (
                  <button
                    onClick={handleFinalize}
                    className="btn-primary flex items-center space-x-3 px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-105"
                  >
                    <Download className="h-5 w-5" />
                    <span className="font-semibold">Finalize & Export</span>
                  </button>
                )}
              </div>

              {/* Main Results Layout */}
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Left: Image with Spine Regions */}
                <div className="lg:col-span-2">
                  <div className="bg-white/60 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-white/20">
                    <SpineVisualizer
                      imageUrl={processingResult.image_url}
                      spineRegions={processingResult.spine_regions}
                      bookMatches={processingResult.book_matches}
                      selectedSpineId={selectedSpineId}
                      onSpineSelected={handleSpineSelected}
                      isFinalized={isFinalized}
                    />
                  </div>
                </div>

                {/* Right: Book List Management */}
                <div className="space-y-6">
                  <BookListManager
                    bookMatches={processingResult.book_matches}
                    ocrFailures={processingResult.ocr_failures}
                    selectedSpineId={selectedSpineId}
                    onSpineSelected={handleSpineSelected}
                    onBookListUpdated={(updatedMatches) => {
                      setProcessingResult(prev => prev ? {
                        ...prev,
                        book_matches: updatedMatches
                      } : null);
                      }}
                    />
                </div>
              </div>

              {/* Finalize Panel */}
              {isFinalized && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  <FinalizePanel
                    processingResult={processingResult}
                    onUnfinalize={handleUnfinalize}
                  />
                </motion.div>
              )}
            </motion.div>
          )}

          {processingState.status === 'error' && (
            <motion.div
              key="error"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.95 }}
              className="text-center"
            >
              <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-xl border border-red-200 max-w-md mx-auto">
                <div className="w-20 h-20 bg-gradient-to-br from-red-400 to-red-600 rounded-full flex items-center justify-center mx-auto mb-6 shadow-lg">
                  <span className="text-3xl">‚ö†Ô∏è</span>
                </div>
                <h3 className="text-xl font-bold text-slate-800 mb-3">
                  Processing Error
                </h3>
                <p className="text-slate-600 mb-6">
                  {processingState.error || 'An unexpected error occurred'}
                </p>
                <button
                  onClick={() => setProcessingState({
                    status: 'idle',
                    progress: 0,
                    message: 'Ready to process images'
                  })}
                  className="btn-primary px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-200 transform hover:scale-105"
                >
                  Try Again
                </button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </main>
    </div>
  );
};

export default App;
