import React, { useCallback, useRef, useEffect, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { toast } from 'react-hot-toast';
import { motion } from 'framer-motion';
import { Upload, Image, AlertCircle } from 'lucide-react';
import { ProcessingState, ProcessingResult } from '../types';
import { apiService } from '../services/api';
import { validateImage } from '../utils/imageUtils';

interface ImageUploaderProps {
  onImageProcessed: (result: ProcessingResult) => void;
  onProcessingStateChange: (state: ProcessingState) => void;
}

const ImageUploader: React.FC<ImageUploaderProps> = ({ onImageProcessed, onProcessingStateChange }) => {
  const eventSourceRef = useRef<EventSource | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<'connecting' | 'connected' | 'disconnected' | 'error'>('disconnected');
  const [isProcessing, setIsProcessing] = useState(false);

  const disconnectEventSource = useCallback(() => {
    if (eventSourceRef.current && !isProcessing) {
      console.log('üîå Manually closing EventSource connection...');
      eventSourceRef.current.close();
      eventSourceRef.current = null;
      setConnectionStatus('disconnected');
    } else if (isProcessing) {
      console.log('‚ö†Ô∏è Skipping EventSource disconnect during processing');
    }
  }, [isProcessing]);

  // Cleanup EventSource on unmount
  const connectEventSource = useCallback(() => {
    // Prevent multiple simultaneous connection attempts
    if (eventSourceRef.current?.readyState === EventSource.CONNECTING) {
      console.log('‚è≥ EventSource connection already in progress, waiting...');
      return new Promise<void>((resolve) => {
        const checkConnection = setInterval(() => {
          if (eventSourceRef.current?.readyState === EventSource.OPEN) {
            clearInterval(checkConnection);
            resolve();
          }
        }, 100);
      });
    }

    // Close any existing connection first
    if (eventSourceRef.current) {
      console.log('üîÑ Closing existing EventSource connection...');
      eventSourceRef.current.close();
      eventSourceRef.current = null;
    }

    console.log('üîå Connecting to EventSource...');
    setConnectionStatus('connecting');
    return new Promise<void>((resolve, reject) => {
      const eventSource = new EventSource('http://127.0.0.1:8002/api/progress');

      // Set a timeout for connection
      const connectionTimeout = setTimeout(() => {
        reject(new Error('EventSource connection timeout'));
      }, 5000);

      eventSource.onopen = () => {
        clearTimeout(connectionTimeout);
        console.log('‚úÖ EventSource connected for progress updates');
        setConnectionStatus('connected');
        resolve();
      };

      // Add connection health check
      const healthCheck = setInterval(() => {
        if (eventSource.readyState === EventSource.CLOSED) {
          clearInterval(healthCheck);
          console.log('üîå EventSource connection lost, attempting to reconnect...');
          setConnectionStatus('disconnected');
          if (eventSourceRef.current === eventSource) {
            setTimeout(() => {
              reconnectEventSource();
            }, 1000);
          }
        }
      }, 1000); // Check more frequently

          eventSource.onmessage = (event) => {
        console.log('üì® EventSource message received:', event.data);
        try {
          const data = JSON.parse(event.data);
          console.log('üîç Parsed data:', data);
          if (data.type === 'progress') {
            console.log('üîÑ Progress update received:', data.progress, data.message);
            // Only update if it's not the initial "Connected to progress stream" message
            if (data.message !== 'Connected to progress stream') {
              console.log('‚úÖ Updating progress to:', data.progress, data.message);
              onProcessingStateChange({
                status: 'processing',
                progress: data.progress,
                message: data.message
              });
            } else {
              console.log('‚è≠Ô∏è Skipping initial connection message');
            }
          } else {
            console.log('‚ö†Ô∏è Unknown message type:', data.type);
          }
        } catch (error) {
          console.error('‚ùå Failed to parse EventSource message:', error);
        }
      };

      eventSource.onerror = (error) => {
        console.error('‚ùå EventSource error:', error);
        console.error('‚ùå EventSource readyState:', eventSource.readyState);
        console.error('‚ùå EventSource URL:', eventSource.url);
        setConnectionStatus('error');
        // Don't reject here, just log the error
      };

      eventSourceRef.current = eventSource;
    });
  }, [onProcessingStateChange]);

  // Add automatic reconnection logic
  const reconnectEventSource = useCallback(async () => {
    try {
      console.log('üîÑ Attempting to reconnect EventSource...');
      await connectEventSource();
      console.log('‚úÖ EventSource reconnected successfully');
    } catch (error) {
      console.error('‚ùå Failed to reconnect EventSource:', error);
      // Try again after a delay
      setTimeout(() => {
        reconnectEventSource();
      }, 2000);
    }
  }, [connectEventSource]);

  // Connect to EventSource immediately when component loads
  useEffect(() => {
    const initConnection = async () => {
      await connectEventSource();
    };
    initConnection();

    return () => {
      disconnectEventSource();
    };
  }, [connectEventSource, disconnectEventSource]);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];

    // Validate image
    const validation = await validateImage(file);

    if (!validation.isValid) {
      toast.error(validation.errors.join(', '));
      return;
    }


    try {
      // Ensure EventSource is connected before starting processing
      try {
        await connectEventSource();
        console.log('‚úÖ EventSource ready for progress updates');
      } catch (error) {
        console.warn('‚ö†Ô∏è EventSource connection failed, continuing without real-time updates:', error);
        // Continue processing even if SSE fails
      }

      // Show initial progress
      onProcessingStateChange({
        status: 'processing',
        progress: 20,
        message: 'Detecting book spines with YOLO...'
      });

      // First test the connection
      console.log('üß™ Testing API connection first...');
      await apiService.testConnection(file);
      console.log('‚úÖ API connection test passed!');

      // Start processing image through the API (don't await - let it run in background)
      console.log('üöÄ Starting image processing...');
      apiService.processImage(file)
        .then(result => {
          console.log('‚úÖ Image processing completed:', result);
          onImageProcessed(result);
          toast.success(`Successfully processed ${result.total_spines || 0} spines!`);

          // Wait a bit for final progress update, then disconnect
          setTimeout(() => {
            onProcessingStateChange({
              status: 'completed',
              progress: 100,
              message: `Processing complete! Found ${result.total_spines || 0} spines with ${result.successful_matches || 0} successful matches.`
            });
            disconnectEventSource();
          }, 1000);
        })
        .catch(error => {
          console.error('‚ùå Image processing failed:', error);
          onProcessingStateChange({
            status: 'error',
            progress: 0,
            message: 'Processing failed',
            error: error instanceof Error ? error.message : 'Unknown error'
          });
          toast.error('Failed to process image');
          disconnectEventSource();
        });

    } catch (error) {
      console.error('Processing error:', error);
      onProcessingStateChange({
        status: 'error',
        progress: 0,
        message: 'Processing failed',
        error: error instanceof Error ? error.message : 'Unknown error'
      });
      toast.error('Failed to process image');

      // Disconnect EventSource after error
      disconnectEventSource();
    }
  }, [onImageProcessed, onProcessingStateChange, connectEventSource, disconnectEventSource]);

  const { getRootProps, getInputProps, isDragActive, isDragReject } = useDropzone({
