import React, { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { BookOpen, AlertCircle } from 'lucide-react';

import { SpineRegion, BookMatch } from '../types';

interface SpineVisualizerProps {
  imageUrl: string;
  spineRegions: SpineRegion[];
  bookMatches: BookMatch[];
  selectedSpineId: string | null;
  onSpineSelected: (spineId: string) => void;
  isFinalized: boolean;
}

const SpineVisualizer: React.FC<SpineVisualizerProps> = ({
  imageUrl,
  spineRegions,
  bookMatches,
  selectedSpineId,
  onSpineSelected,
  isFinalized
}) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [hoveredSpineId, setHoveredSpineId] = useState<string | null>(null);
  const imageRef = useRef<HTMLImageElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // Get book match for a spine region
  const getBookForSpine = (spineId: string): BookMatch | undefined => {
    return bookMatches.find(book => book.spine_region_id === spineId);
  };

  // Get spine region by ID
  const getSpineRegion = (spineId: string): SpineRegion | undefined => {
    return spineRegions.find(spine => spine.id === spineId);
  };

  // Handle spine region click
  const handleSpineClick = (spineId: string) => {
    onSpineSelected(spineId);
  };

  // Handle spine region hover
  const handleSpineHover = (spineId: string | null) => {
    setHoveredSpineId(spineId);
  };

  // Calculate spine region styles
  const getSpineStyles = (spine: SpineRegion) => {
    const book = getBookForSpine(spine.id);
    const isSelected = selectedSpineId === spine.id;
    const isHovered = hoveredSpineId === spine.id;

    let baseClasses = 'spine-region';
    if (isSelected) baseClasses += ' selected';
    if (isHovered) baseClasses += ' hovered';

    const transform = `rotate(${spine.rotation}deg)`;

    return {
      className: baseClasses,
      style: {
        left: `${spine.x}px`,
        top: `${spine.y}px`,
        width: `${spine.width}px`,
        height: `${spine.height}px`,
        transform,
        zIndex: isSelected ? 10 : isHovered ? 5 : 1
      }
    };
  };

  // Render spine region
  const renderSpineRegion = (spine: SpineRegion) => {
    const book = getBookForSpine(spine.id);
    const styles = getSpineStyles(spine);
    const spineNumber = spineRegions.findIndex(s => s.id === spine.id) + 1;

    return (
      <motion.div
        key={spine.id}
        className={styles.className}
        style={styles.style}
        onClick={() => handleSpineClick(spine.id)}
        onMouseEnter={() => handleSpineHover(spine.id)}
        onMouseLeave={() => handleSpineHover(null)}
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.2 }}
      >
        {/* Spine Number Label */}
        <div className="spine-label">
          {spineNumber}
        </div>

        {/* Confidence Indicator */}
        <div className="absolute -bottom-6 left-0 text-xs text-gray-600">
          {Math.round(spine.confidence * 100)}%
        </div>

        {/* Hover Tooltip */}
        {isHovered && book && (
          <motion.div
            className="absolute -top-16 left-0 bg-gray-900 text-white text-sm px-3 py-2 rounded-lg shadow-lg whitespace-nowrap z-20"
            initial={{ opacity: 0, y: 5 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.2 }}
          >
            <div className="font-medium">{book.title}</div>
            <div className="text-gray-300 text-xs">
              by {book.author_name.join(', ')}
            </div>
            {book.first_publish_year && (
              <div className="text-gray-300 text-xs">
                {book.first_publish_year}
              </div>
            )}
            <div className="absolute top-full left-4 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900"></div>
          </motion.div>
        )}
      </motion.div>
    );
  };

  return (
    <div className="card p-0 overflow-hidden">
      {/* Header */}
      <div className="p-6 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900 mb-2">
          Book Spine Detection
        </h3>
        <p className="text-sm text-gray-600">
          {spineRegions.length} spine{spineRegions.length !== 1 ? 's' : ''} detected
          {bookMatches.length > 0 && ` â€¢ ${bookMatches.length} book${bookMatches.length !== 1 ? 's' : ''} identified`}
        </p>
      </div>

      {/* Image Container */}
      <div 
        ref={containerRef}
        className="relative bg-gray-100 min-h-[400px] flex items-center justify-center"
      >
        {!imageLoaded && (
          <div className="text-center text-gray-500">
            <BookOpen className="w-12 h-12 mx-auto mb-2 text-gray-400" />
            <p>Loading image...</p>
          </div>
        )}

        {/* Main Image */}
        <img
          ref={imageRef}
          src={imageUrl}
          alt="Book spines"
          className="max-w-full max-h-[600px] object-contain"
          onLoad={() => setImageLoaded(true)}
          onError={() => setImageLoaded(false)}
        />

        {/* Spine Regions Overlay */}
        {imageLoaded && (
          <div className="absolute inset-0 pointer-events-none">
            {spineRegions.map(renderSpineRegion)}
          </div>
        )}

        {/* No Spines Detected */}
        {imageLoaded && spineRegions.length === 0 && (
          <div className="absolute inset-0 flex items-center justify-center bg-white bg-opacity-90">
            <div className="text-center text-gray-500">
              <AlertCircle className="w-12 h-12 mx-auto mb-2 text-gray-400" />
              <p className="text-lg font-medium">No book spines detected</p>
              <p className="text-sm">Try uploading a clearer image with better lighting</p>
            </div>
          </div>
        )}
      </div>

      {/* Legend */}
      {spineRegions.length > 0 && (
        <div className="p-4 bg-gray-50 border-t border-gray-200">
          <div className="flex items-center justify-between text-sm text-gray-600">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-primary-500 rounded"></div>
                <span>Detected spine</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-primary-600 rounded ring-2 ring-primary-400"></div>
                <span>Selected</span>
              </div>
            </div>
            
            <div className="text-xs">
              Click on spine regions to select books
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SpineVisualizer;
