import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { BookOpen, Edit3, Trash2, Search, AlertTriangle, Plus } from 'lucide-react';

import { BookMatch, OCRFailure } from '../types';

interface BookListManagerProps {
  bookMatches: BookMatch[];
  ocrFailures: OCRFailure[];
  selectedSpineId: string | null;
  onSpineSelected: (spineId: string) => void;
  onBookListUpdated: (updatedMatches: BookMatch[]) => void;
}

const BookListManager: React.FC<BookListManagerProps> = ({
  bookMatches,
  ocrFailures,
  selectedSpineId,
  onSpineSelected,
  onBookListUpdated
}) => {
  const [editingBookId, setEditingBookId] = useState<string | null>(null);
  const [showSearchModal, setShowSearchModal] = useState(false);

  // Handle book selection
  const handleBookClick = (book: BookMatch) => {
    onSpineSelected(book.spine_region_id);
  };

  // Handle book deletion
  const handleDeleteBook = (bookId: string) => {
    const updatedMatches = bookMatches.filter(book => book.id !== bookId);
    onBookListUpdated(updatedMatches);
  };

  // Handle OCR failure deletion
  const handleDeleteOCRFailure = (failureId: string) => {
    // This would update the OCR failures list
    console.log('Delete OCR failure:', failureId);
  };

  // Get match type color
  const getMatchTypeColor = (matchType: string) => {
    switch (matchType) {
      case 'exact':
        return 'bg-success-100 text-success-800';
      case 'strong':
        return 'bg-primary-100 text-primary-800';
      case 'moderate':
        return 'bg-warning-100 text-warning-800';
      case 'weak':
      case 'poor':
        return 'bg-error-100 text-error-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Get match type label
  const getMatchTypeLabel = (matchType: string) => {
    switch (matchType) {
      case 'exact':
        return 'Perfect Match';
      case 'strong':
        return 'Strong Match';
      case 'moderate':
        return 'Moderate Match';
      case 'weak':
        return 'Weak Match';
      case 'poor':
        return 'Poor Match';
      default:
        return 'Unknown';
    }
  };

  return (
    <div className="space-y-6">
      {/* Book Matches */}
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">
            Identified Books
          </h3>
          <span className="text-sm text-gray-500 bg-gray-100 px-2 py-1 rounded-full">
            {bookMatches.length}
          </span>
        </div>

        {bookMatches.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <BookOpen className="w-12 h-12 mx-auto mb-2 text-gray-400" />
            <p>No books identified yet</p>
          </div>
        ) : (
          <div className="space-y-3">
            <AnimatePresence>
              {bookMatches.map((book, index) => (
                <motion.div
                  key={book.id}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2, delay: index * 0.05 }}
                  className={`
                    p-4 border rounded-lg cursor-pointer transition-all duration-200
                    ${selectedSpineId === book.spine_region_id
                      ? 'border-primary-500 bg-primary-50 ring-2 ring-primary-200'
                      : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                    }
                  `}
                  onClick={() => handleBookClick(book)}
                >
                  {/* Book Header */}
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex-1 min-w-0">
                      <h4 className="font-medium text-gray-900 truncate">
                        {book.title}
                      </h4>
                      <p className="text-sm text-gray-600">
                        by {book.author_name.join(', ')}
                      </p>
                    </div>
                    
                    {/* Match Type Badge */}
                    <span className={`text-xs px-2 py-1 rounded-full ml-2 ${getMatchTypeColor(book.match_type)}`}>
                      {getMatchTypeLabel(book.match_type)}
                    </span>
                  </div>

                  {/* Book Details */}
                  <div className="flex items-center justify-between text-sm text-gray-500 mb-3">
                    <div className="flex items-center space-x-4">
                      {book.first_publish_year && (
                        <span>{book.first_publish_year}</span>
                      )}
                      {book.publisher && (
                        <span className="truncate max-w-[120px]" title={book.publisher}>
                          {book.publisher}
                        </span>
                      )}
                    </div>
                    
                    <div className="flex items-center space-x-2">
                      <span className="text-xs bg-gray-100 px-2 py-1 rounded">
                        {Math.round(book.match_score * 100)}%
                      </span>
                    </div>
                  </div>

                  {/* OCR Text (Hidden from user as requested) */}
                  <div className="text-xs text-gray-400 bg-gray-50 p-2 rounded border">
                    <span className="font-medium">OCR Text:</span> {book.ocr_text}
                  </div>

                  {/* Action Buttons */}
                  <div className="flex items-center justify-end space-x-2 mt-3 pt-3 border-t border-gray-100">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        setEditingBookId(book.id);
                        setShowSearchModal(true);
                      }}
                      className="btn-secondary text-xs py-1 px-2 flex items-center space-x-1"
                    >
                      <Search className="w-3 h-3" />
                      <span>Search</span>
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteBook(book.id);
                      }}
                      className="btn-danger text-xs py-1 px-2 flex items-center space-x-1"
                    >
                      <Trash2 className="w-3 h-3" />
                      <span>Delete</span>
                    </button>
                  </div>
                </motion.div>
              ))}
            </AnimatePresence>
          </div>
        )}
      </div>

      {/* OCR Failures */}
      {ocrFailures.length > 0 && (
        <div className="card">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">
              OCR Failures
            </h3>
            <span className="text-sm text-gray-500 bg-warning-100 text-warning-800 px-2 py-1 rounded-full">
              {ocrFailures.length}
            </span>
          </div>

          <div className="space-y-3">
            <AnimatePresence>
              {ocrFailures.map((failure, index) => (
                <motion.div
                  key={failure.id}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2, delay: index * 0.05 }}
                  className="p-4 border border-warning-200 rounded-lg bg-warning-50"
                >
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex items-center space-x-2">
                      <AlertTriangle className="w-4 h-4 text-warning-600" />
                      <span className="font-medium text-warning-800">
                        {failure.error_type === 'no_text_detected' && 'No Text Detected'}
                        {failure.error_type === 'low_confidence' && 'Low Confidence'}
                        {failure.error_type === 'processing_error' && 'Processing Error'}
                      </span>
                    </div>
                    
                    <button
                      onClick={() => handleDeleteOCRFailure(failure.id)}
                      className="btn-danger text-xs py-1 px-2"
                    >
                      <Trash2 className="w-3 h-3" />
                    </button>
                  </div>

                  <p className="text-sm text-warning-700 mb-3">
                    {failure.message}
                  </p>

                  <div className="flex items-center justify-between">
                    <button
                      onClick={() => {
                        // TODO: Implement manual entry modal
                        console.log('Manual entry for:', failure.id);
                      }}
                      className="btn-secondary text-xs py-1 px-2 flex items-center space-x-1"
                    >
                      <Plus className="w-3 h-3" />
                      <span>Manual Entry</span>
                    </button>
                    
                    <button
                      onClick={() => {
                        // TODO: Implement search for this spine
                        console.log('Search for spine:', failure.id);
                      }}
                      className="btn-primary text-xs py-1 px-2 flex items-center space-x-1"
                    >
                      <Search className="w-3 h-3" />
                      <span>Search</span>
                    </button>
                  </div>
                </motion.div>
              ))}
            </AnimatePresence>
          </div>
        </div>
      )}

      {/* Quick Actions */}
      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">
          Quick Actions
        </h3>
        
        <div className="space-y-3">
          <button
            onClick={() => setShowSearchModal(true)}
            className="w-full btn-primary flex items-center justify-center space-x-2"
          >
            <Search className="w-4 h-4" />
            <span>Search Open Library</span>
          </button>
          
          <button
            onClick={() => {
              // TODO: Implement manual book addition
              console.log('Add manual book entry');
            }}
            className="w-full btn-secondary flex items-center justify-center space-x-2"
          >
            <Plus className="w-4 h-4" />
            <span>Add Manual Entry</span>
          </button>
        </div>
      </div>

      {/* Search Modal Placeholder */}
      {showSearchModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">Search Open Library</h3>
            <p className="text-gray-600 mb-4">
              This feature is coming soon! You'll be able to search for books and replace incorrect matches.
            </p>
            <button
              onClick={() => setShowSearchModal(false)}
              className="btn-primary w-full"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookListManager;
