import React, { useState } from 'react';
import { Toaster } from 'react-hot-toast';
import { motion, AnimatePresence } from 'framer-motion';
import { Upload, BookOpen, Settings, Download } from 'lucide-react';

import { ProcessingState, ProcessingResult } from './types';
import ImageUploader from './components/ImageUploader';
import SpineVisualizer from './components/SpineVisualizer';
import BookListManager from './components/BookListManager';
import ProcessingStatus from './components/ProcessingStatus';
import FinalizePanel from './components/FinalizePanel';

const App: React.FC = () => {
  const [processingState, setProcessingState] = useState<ProcessingState>({
    status: 'idle',
    progress: 0,
    message: 'Ready to process images'
  });
  
  const [processingResult, setProcessingResult] = useState<ProcessingResult | null>(null);
  const [selectedSpineId, setSelectedSpineId] = useState<string | null>(null);
  const [isFinalized, setIsFinalized] = useState(false);

  const handleImageProcessed = (result: ProcessingResult) => {
    setProcessingResult(result);
    setProcessingState({
      status: 'completed',
      progress: 100,
      message: `Processed ${result.total_spines} spines with ${result.successful_matches} successful matches`
    });
  };

  const handleSpineSelected = (spineId: string) => {
    setSelectedSpineId(spineId);
  };

  const handleFinalize = () => {
    setIsFinalized(true);
  };

  const handleUnfinalize = () => {
    setIsFinalized(false);
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      {/* Animated Background */}
      <div className="fixed inset-0 bg-black">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(120,119,198,0.3),transparent_50%)]"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_80%_20%,rgba(255,119,198,0.2),transparent_50%)]"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_20%_80%,rgba(120,255,198,0.2),transparent_50%)]"></div>
        
        {/* Floating Particles */}
        <div className="absolute top-20 left-20 w-2 h-2 bg-cyan-400 rounded-full animate-pulse"></div>
        <div className="absolute top-40 right-32 w-1 h-1 bg-pink-400 rounded-full animate-pulse" style={{animationDelay: '1s'}}></div>
        <div className="absolute bottom-32 left-1/4 w-1.5 h-1.5 bg-purple-400 rounded-full animate-pulse" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/2 right-20 w-1 h-1 bg-green-400 rounded-full animate-pulse" style={{animationDelay: '0.5s'}}></div>
      </div>
      
      <Toaster position="top-right" />
      
      {/* Header */}
      <header className="relative z-10 border-b border-white/10 bg-black/20 backdrop-blur-xl">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-24">
            <div className="flex items-center space-x-6">
              <div className="relative group">
                <div className="w-16 h-16 bg-gradient-to-br from-cyan-400 via-purple-500 to-pink-500 rounded-3xl shadow-2xl flex items-center justify-center transform rotate-6 group-hover:rotate-0 transition-all duration-500 group-hover:scale-110">
                  <div className="w-12 h-12 bg-black/20 rounded-2xl flex items-center justify-center">
                    <BookOpen className="h-8 w-8 text-white" />
                  </div>
                </div>
                <div className="absolute -top-2 -right-2 w-6 h-6 bg-gradient-to-br from-green-400 to-emerald-500 rounded-full border-4 border-black shadow-lg animate-bounce"></div>
                <div className="absolute -bottom-1 -left-1 w-4 h-4 bg-gradient-to-br from-orange-400 to-red-500 rounded-full border-2 border-black shadow-md animate-pulse"></div>
              </div>
              <div>
                <h1 className="text-6xl font-black bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent tracking-tighter leading-none">
                  SPINECAT
                </h1>
                <p className="text-lg text-gray-300 -mt-2 font-light tracking-wider">NEURAL SPINE DETECTION</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button className="p-4 rounded-2xl bg-white/5 hover:bg-white/10 transition-all duration-300 border border-white/10 hover:border-white/20 backdrop-blur-sm group">
                <Settings className="h-6 w-6 text-gray-300 group-hover:text-white transition-colors" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        <AnimatePresence mode="wait">
          {processingState.status === 'idle' && (
            <motion.div
              key="upload"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              <ImageUploader
                onImageProcessed={handleImageProcessed}
                onProcessingStateChange={setProcessingState}
              />
            </motion.div>
          )}

          {processingState.status === 'processing' && (
            <motion.div
              key="processing"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="text-center"
            >
              <ProcessingStatus state={processingState} />
            </motion.div>
          )}

          {processingState.status === 'completed' && processingResult && (
            <motion.div
              key="results"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="space-y-6"
            >
              {/* Results Header */}
              <div className="flex items-center justify-between bg-gradient-to-r from-cyan-900/20 via-purple-900/20 to-pink-900/20 backdrop-blur-xl rounded-3xl p-8 border border-cyan-500/20 shadow-2xl">
                <div>
                  <h2 className="text-5xl font-black bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent tracking-tighter">
                    PROCESSING COMPLETE
                  </h2>
                  <div className="flex items-center space-x-6 mt-4">
                    <span className="inline-flex items-center px-4 py-2 rounded-2xl text-sm font-bold bg-cyan-500/20 text-cyan-300 border border-cyan-400/30 backdrop-blur-sm">
                      üìö {processingResult.total_spines} SPINES DETECTED
                    </span>
                    <span className="inline-flex items-center px-4 py-2 rounded-2xl text-sm font-bold bg-green-500/20 text-green-300 border border-green-400/30 backdrop-blur-sm">
                      ‚úÖ {processingResult.successful_matches} SUCCESSFUL MATCHES
                    </span>
                  </div>
                </div>
                
                {!isFinalized && (
                  <button
                    onClick={handleFinalize}
                    className="group relative px-8 py-4 bg-gradient-to-r from-cyan-500 to-purple-600 hover:from-cyan-400 hover:to-purple-500 rounded-2xl shadow-2xl hover:shadow-cyan-500/25 transition-all duration-300 transform hover:scale-105 border border-cyan-400/30"
                  >
                    <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-purple-500 rounded-2xl blur-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    <div className="relative flex items-center space-x-3">
                      <Download className="h-6 w-6 text-white" />
                      <span className="font-black text-white tracking-wider">FINALIZE & EXPORT</span>
                    </div>
                  </button>
                )}
              </div>

              {/* Main Results Layout */}
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
                {/* Left: Image with Spine Regions */}
                <div className="lg:col-span-2">
                  <div className="bg-gradient-to-br from-cyan-900/10 via-purple-900/10 to-pink-900/10 backdrop-blur-xl rounded-3xl p-8 border border-cyan-500/20 shadow-2xl">
                    <SpineVisualizer
                      imageUrl={processingResult.image_url}
                      spineRegions={processingResult.spine_regions}
                      bookMatches={processingResult.book_matches}
                      selectedSpineId={selectedSpineId}
                      onSpineSelected={handleSpineSelected}
                      isFinalized={isFinalized}
                    />
                  </div>
                </div>

                {/* Right: Book List Management */}
                <div className="space-y-8">
                  <BookListManager
                    bookMatches={processingResult.book_matches}
                    ocrFailures={processingResult.ocr_failures}
                    selectedSpineId={selectedSpineId}
                    onSpineSelected={handleSpineSelected}
                    onBookListUpdated={(updatedMatches) => {
                      setProcessingResult(prev => prev ? {
                        ...prev,
                        book_matches: updatedMatches
                      } : null);
                      }}
                    />
                </div>
              </div>

              {/* Finalize Panel */}
              {isFinalized && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  <FinalizePanel
                    processingResult={processingResult}
                    onUnfinalize={handleUnfinalize}
                  />
                </motion.div>
              )}
            </motion.div>
          )}

          {processingState.status === 'error' && (
            <motion.div
              key="error"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.95 }}
              className="text-center"
            >
              <div className="bg-gradient-to-br from-red-900/20 via-orange-900/20 to-red-900/20 backdrop-blur-xl rounded-3xl p-10 border border-red-500/30 shadow-2xl max-w-lg mx-auto">
                <div className="w-24 h-24 bg-gradient-to-br from-red-500 to-orange-600 rounded-full flex items-center justify-center mx-auto mb-8 shadow-2xl border-4 border-red-400/30">
                  <span className="text-4xl">‚ö†Ô∏è</span>
                </div>
                <h3 className="text-2xl font-black text-red-300 mb-4 tracking-wider">
                  PROCESSING ERROR
                </h3>
                <p className="text-gray-300 mb-8 text-lg">
                  {processingState.error || 'An unexpected error occurred'}
                </p>
                <button
                  onClick={() => setProcessingState({
                    status: 'idle',
                    progress: 0,
                    message: 'Ready to process images'
                  })}
                  className="group relative px-8 py-4 bg-gradient-to-r from-red-500 to-orange-600 hover:from-red-400 hover:to-orange-500 rounded-2xl shadow-2xl hover:shadow-red-500/25 transition-all duration-300 transform hover:scale-105 border border-red-400/30"
                >
                  <div className="absolute inset-0 bg-gradient-to-r from-red-400 to-orange-500 rounded-2xl blur-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                  <div className="relative font-black text-white tracking-wider">
                    TRY AGAIN
                  </div>
                </button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </main>
    </div>
  );
};

export default App;
